import { useRef, useState } from "react";
import { Prompt } from "react-router-dom";
import Card from "../UI/Card";
import LoadingSpinner from "../UI/LoadingSpinner";
import classes from "./QuoteForm.module.css";

const QuoteForm = (props) => {
  let [entered, setEntered] = useState(false);
  let [isValid, setValid] = useState(false);
  let [valueAuthor, setAuthor] = useState(false);
  let [valueText, setText] = useState(false);
  function submitFormHandler(event) {
    event.preventDefault();

    props.onAddQuote({ author: valueAuthor, text: valueText });
  }
  if (!authorInputRef.current.value && !textInputRef.current.value) {
    setValid(true);
  }
  let handleClick = () => {
    setEntered(false);
  };
  let handleFocus = () => {
    setEntered(true);
  };

  return (
    <Card>
      <Prompt when={entered} message="Are you sure?" />
      <form
        className={classes.form}
        onSubmit={submitFormHandler}
        onFocus={handleFocus}
      >
        {props.isLoading && (
          <div className={classes.loading}>
            <LoadingSpinner />
          </div>
        )}

        <div className={classes.control}>
          <label htmlFor="author">Author</label>
          <input type="text" id="author" value={v} />
        </div>
        <div className={classes.control}>
          <label htmlFor="text">Text</label>
          <textarea id="text" rows="5" ref={textInputRef}></textarea>
        </div>
        <div className={classes.actions}>
          <button
            className={isValid ? "btn" : "btn invalid"}
            onClick={handleClick}
            disabled={!isValid}
          >
            Add Quote
          </button>
        </div>
      </form>
    </Card>
  );
};

export default QuoteForm;
