import { useParams, Route, Link, Switch } from "react-router-dom";
import Comments from "../components/comments/Comments";
import HighlightedQuote from "../components/quotes/HighlightedQuote";
import NoQuotesFound from "../components/quotes/NoQuotesFound";
import useHttp from "../hooks/use-http";
import { useEffect } from "react";
import { getSingleQuote } from "../lib/api";
let DetailQuotes = () => {
  let { sendRequest, status, data, error } = useHttp(getSingleQuote);
  let params = useParams();
  let dummy_data = [
    { id: "p1", author: "murakami", text: "always be yourself" },
    { id: "p2", author: "remark", text: "love" },
  ];
  useEffect(() => {
    sendRequest(params.quoteId);
  }, [sendRequest, params.quoteId]);
  console.log(data);

 if (status === "pending") {
  return (
    <div className="centered">
      <LoadingSpinner />
    </div>
  );
}
if (error) {
  return <p className="centered focused">{error}</p>;
}
if (status === "completed" && (!data || data.length === 0)) {
  return <NoQuotesFound />;
} 
  return (
    <div>
      <HighlightedQuote quote={data} />
      <Switch>
        <Route path={`/allQuotes/${data.id}/`} exact>
          <div className="centered">
            <Link to={`/allQuotes/${data.id}/comments`} className="btn--flat ">
              Add Comments
            </Link>
          </div>
        </Route>
        <Route path={`/allQuotes/${data.id}/comments`}>
          <Comments />
        </Route>
      </Switch>
    </div>
  );
};
export default DetailQuotes;
