import QuoteList from "../components/quotes/QuoteList";
import useHttp from "../hooks/use-http";
import { useEffect } from "react";
import { getAllQuotes } from "../lib/api";
let dummy_data = [
  { id: "p1", author: "murakami", text: "always be yourself" },
  { id: "p2", author: "amark", text: "love" },
];

let AllQuotes = () => {
  let { sendRequest, status, data, error } = useHttp(getAllQuotes);

  useEffect(() => {
    sendRequest();
  }, [sendRequest]);
  if (data.length === 0) {
    data = "nothing";
  }
  if(status==="completed" && data.length===0){}
  return (
    <div>
      <QuoteList quotes={data} />
    </div>
  );
};
export default AllQuotes;
