import QuoteList from "../components/quotes/QuoteList";
import useHttp from "../hooks/use-http";
import { useEffect, useState } from "react";
import { getAllQuotes } from "../lib/api";
import LoadingSpinner from "../components/UI/LoadingSpinner";
import NoQuotesFound from "../components/quotes/NoQuotesFound";

let AllQuotes = () => {
  let { sendRequest, status, data, error } = useHttp(getAllQuotes, true);
  let [newData, setData] = useState(false);
  let handleDelete = (id) => {
    fetch(
      `https://react1-9a97e-default-rtdb.firebaseio.com/quotes/${id}.json`,
      {
        method: "DELETE",
      }
    );
    fetch(`https://react1-9a97e-default-rtdb.firebaseio.com/quotes.json`)
      .then((response) => response.json())
      .then((fData) => setData(fData));
    const transformedQuotes = [];

    for (const key in data) {
      const quoteObj = {
        id: key,
        ...data[key],
      };

      transformedQuotes.push(quoteObj);
    }
    setData(transformedQuotes);

    console.log(9);
  };
  useEffect(() => {
    sendRequest();
  }, [sendRequest]);

  if (status === "pending") {
    return (
      <div className="centered">
        <LoadingSpinner />
      </div>
    );
  }

  if (error) {
    return <p className="centered focused">{error}</p>;
  }
  if (status === "completed" && (!data || data.length === 0)) {
    return <NoQuotesFound />;
  } else {
    return (
      <QuoteList
        quotes={newData ? newData : data}
        onHandleDelete={handleDelete}
      />
    );
  }
};
export default AllQuotes;
