import classes from "./CommentItem.module.css";

const CommentItem = (props) => {
  let handleDelete = () => {
    fetch(
      `https://react1-9a97e-default-rtdb.firebaseio.com/comments/${props.itemsId}/${props.id}.json`,
      {
        method: "DELETE",
      }
    ).then(() => {
      props.onFetch(Math.random());
    });
  };

  return (
    <li className={classes.item} onClick={handleDelete}>
      <p>{props.text}</p>
    </li>
  );
};

export default CommentItem;
